package com.zyl.mp3cutter.home.bean;public class MusicInfo {	private int id;	private String filepath;	private String filename;	public int getId() {		return id;	}	public void setId(int id) {		this.id = id;	}	public String getFilepath() {		return filepath;	}	public void setFilepath(String filepath) {		this.filepath = filepath;	}	public String getFilename() {		return filename;	}	public void setFilename(String filename) {		this.filename = filename;	}}